import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import "./App.scss";
import { Contador } from "./Componentes/Contador/Contador";
import { Form } from "./Componentes/Form/Form";
import { Sub } from "./types";
interface AppState {
  subs: Array<Sub>;
  newSubNumber: number;
}
// un array de objetos que entiendne el contrato de sub.
const INITIAL_STATE = [
  {
    nick: "dapelu",
    subMonths: 4,
    avatar: "https://i.pravatar.cc/250",
    description: " admin de un canal en youtube",
  },
  {
    nick: "Darko",
    subMonths: 5,
    avatar: "https://i.pravatar.cc/650",
    description: "Estudiante avanzado ",
  },
];

function App() {
  const [subs, setSubs] = useState<AppState["subs"]>([]);
  const [newSubNumber, setNewSubNumber] = useState<AppState["newSubNumber"]>(0);

  useEffect(() => {}, []);

  return (
    <>
      <Contador />
      <h1> Subs </h1>
      <ul>
        {subs.map((sub) => {
          return (
            <li key={sub.nick}>
              <img src={sub.avatar} alt={"avatar for " + sub.nick} />
              <h4>
                {sub.nick} (<small> {sub.subMonths} </small>)
              </h4>
              <p> {sub.description.substring(0, 100)} </p>
            </li>
          );
        })}
      </ul>
      <Form onNewSub={setSubs} />
    </>
  );
}

export default App;
