import "./SForm.scss";

import React, { useState } from "react";
//en el useState vemos un bojeto que recibe un nick, submounth, avatar y descripcion
export const Form: React.FC = ({}) => {
  const [inputValues, setInputValues] = useState({
    nick: "",
    subMounth: 0,
    avatar: "",
    descripcion: "",
  });

  const handleSubmit = () => {};
  // se usa la propagacion de objetos y luego se usa el event target
  // donde solo el event.target.name que cambia se actuliza su valor
  // el resto queda igual.
  const handleCHange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValues({ ...inputValues, [event.target.name]: event.target.value });
    console.log(
      "e.target.name : " +
        event.target.name +
        "e.target.value:" +
        event.target.value
    );
  };

  const handleButton = () => {
    <h1> hola hiciste click !!</h1>;
  };
  return (
    <>
      <div>
        <form onSubmit={handleSubmit} id="formS">
          <input
            onChange={handleCHange}
            value={inputValues.nick}
            type="text"
            name="nick"
            placeholder="nick"
          />
          <input
            onChange={handleCHange}
            value={inputValues.subMounth}
            type="text"
            name="subMounth"
            placeholder="subMounth"
          />
          <input
            onChange={handleCHange}
            value={inputValues.avatar}
            type="text"
            name="avatar"
            placeholder="avatar"
          />
          <textarea
            onChange={handleCHange}
            value={inputValues.descripcion}
            name="descripcion"
            placeholder="descripcion"
          />
          <button onClick={handleButton}> guardar nuevo suscriptor</button>
        </form>
      </div>
    </>
  );
};
