import React, { useEffect, useRef, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import "./App.scss";
import { Contador } from "./Componentes/Contador/Contador";
import { Form } from "./Componentes/Form/Form";
import { Sub } from "./types";
import { List } from "./Componentes/List/list";

// recomendacion tener una interfaz donde cada propiedad sean los
// posibles estados de ese componente.

interface AppState {
  subs: Array<Sub>;
  newSubNumber: number;
}
// un array de objetos que entiendne el contrato de sub.
const INITIAL_STATE = [
  {
    nick: "Dylan Cafferatas",
    subMonths: 4,
    avatar: "https://i.pravatar.cc/250",
    description: " Vivve en miami",
  },
  {
    nick: "Darko",
    subMonths: 5,
    avatar: "https://i.pravatar.cc/650",
    description: "Vivve en la villa 1-11-14 ",
  },
];

function App() {
  // <Array<Sub> > array de subs, o tambien  <Sub[]> como quieras.
  const [subs, setSubs] = useState<AppState["subs"]>([]);
  const [newSubNumber, setNewSubNumber] = useState<AppState["newSubNumber"]>(0);
  const divRef = useRef(0);

  useEffect(() => {
    setSubs(INITIAL_STATE);
  }, []);
  //Escucha otra manera en lguar de pasar setSubs pasar directamente el handle asi encapsular lo que vamos a hacer.
  // aca por testing en lugar de enviar las entra침as que usamos, le enviamos mas masticado, util con los customHooks.
  const handleNewSub = (newSub: Sub) => {
    setSubs((subs) => [...subs, newSub]);
  };

  return (
    <>
      <Contador />
      <h1> Subs </h1>
      <List subs={subs}>
        {" "}
        <h1>pepito Hola que onda </h1>{" "}
      </List>
      <Form onNewSub={handleNewSub} />
    </>
  );
}

export default App;
